import React from 'react'
import * as styles from './styles'

export interface RentProductProps{
  productName: string;
  imgUrl: string;
  description: string;
  price: string;
}

const RentProduct: React.FC<RentProductProps> = ( {imgUrl, productName, description ,price} ) => {

  var day = new Date();
  var dd = day.getDate().toString();
  var mm = (day.getMonth()+1).toString();
  var yyyy = (day.getFullYear()).toString();
  if(parseInt(dd) < 10){
    dd = '0' + dd;
  } 
  if(parseInt(mm)<10){
    mm = '0' + mm;
  } 
  const today = yyyy+'-'+mm+'-'+dd;

  return(
    <div className={styles.productDisplay}>
      <h1>Aluguel {productName}</h1>
      <img src={imgUrl} alt={productName} className={styles.productImage}></img>
      <h3>{productName}</h3>
      <p>{description}</p>
      <h2 className={styles.price}>R${price}/Dia</h2>

      <div>
        Data de aluguel: 
        <input type="date" name="datadenascimento" min={today} max="2022-01-01"/>
      </div>

      <div>
        Data de devolução: 
        <input type="date" name="datadenascimento" min={today} max="2022-01-01"/>
      </div>

      <button className={styles.rentButton}>Alugar</button>
    </div>
  );
}

export default RentProduct;
