import { css } from '@emotion/css'

export const purchaseDisplay = css `
  padding: 10px;
  font-family: 'Bitter', serif;
  margin: 10px;
  h2{color: rgb(92, 182, 3)}
  ul{
    padding: 0;
  }
  li{
    margin: 5px;
    display: block;
    border: 3px solid #B5E550;
    border-radius: 10px;
    padding: 5px;
  } 
`;
