import { css } from '@emotion/css';

export const productImage = css `
  height: 200px;
`;

export const content = css `
  * {
    margin: 0px;
  }
`;

export const buttons = css `
  display: flex;
  flex-direction: column;

  > button{
    margin: 5px;
  }
`;
