import { css } from "@emotion/css";

export const product = css`
  margin: 30px;

  a{
    text-decoration: none;
  }
`;

export const productImage = css`
  height: 200px;
`;

export const price = css`
  color: rgb(92, 182, 3);
`;

export const title = css `
  margin-bottom: 0px;
  color: black;
`;

export const cartIcon = css `
  margin-top: 10px;
`;
