import React from 'react';
import { ProductModel } from '../../../../../store/types';
import  ManageButton  from '../../../../../base-components/ManageButton';
import * as styles from './styles'

export interface InventoryItemProps{
  product: ProductModel;
}

const InventoryItem: React.FC <InventoryItemProps> = ({ product }) => {
  return(
    <div>
      <li className={styles.content}>
        <img src={product.img} alt="" className={styles.productImage}/> 
        <h3>{product.name}</h3>
        <h2>{product.quantity} unidades em estoque</h2>
        <h2>R${product.price}</h2>
        <div className={styles.buttons}>
          <ManageButton text={"Adicionar estoque"} />
          <ManageButton text={"Remover estoque"} />
          <ManageButton text={"Atualizar estoque"} />
        </div>
      </li>
    </div>
  );
}

export default InventoryItem;
